defaultOptionValue:
{{ ((options, serverSideFiltering) => ( 
    (() => {
        const loggedInTeamID = appsmith.store.currentTeamID;
        if (!loggedInTeamID) return []; // Tránh lỗi nếu chưa có currentTeamID

        const selectedPartners = [];
        (getEvaluationPairs.data || []).forEach(pair => {
            if (pair.TeamA_ID === loggedInTeamID) {
                selectedPartners.push(pair.TeamB_ID);
            } else if (pair.TeamB_ID === loggedInTeamID) {
                selectedPartners.push(pair.TeamA_ID);
            }
        });
        return selectedPartners;
    })()
))(MyPartnerSelector.options, MyPartnerSelector.serverSideFiltering) }}

onOptionChange:
{{
    PairingLogic.updateSelectionsForTeam(
        appsmith.store.currentTeamID, // ID của đội hiện tại đang thực hiện lựa chọn
        this.selectedOptionValues // Hoặc this.selectedValues cho Checkbox Group
    )
}}

sourceData:
{{
    (getTeams.data || [])
        .filter(team => team.TeamID !== appsmith.store.currentTeamID) // Lọc bỏ đội hiện tại
        .map(team => ({ label: team.TeamName, value: team.TeamID }))
}}
